mapper.select:
select ap_seq, fk_apform_no, fk_mbr_seq, ap_dept, ap_progress, ap_title, ap_contents, ap_approver, ap_manage_approver , ap_referrer, ap_start_day, ap_end_day, apform_name, mbr_name from ( select rownum AS rno, ap_seq, fk_apform_no, fk_mbr_seq, ap_dept, ap_progress, ap_title, ap_contents, ap_approver, ap_manage_approver , ap_referrer, ap_start_day, ap_end_day, apform_name, mbr_name from ( select ap_seq, fk_apform_no, fk_mbr_seq, ap_progress, ap_title, ap_contents, ap_approver, ap_manage_approver , ap_referrer, to_char(ap_start_day,'YYYY-MM-DD') as ap_start_day, to_char(ap_end_day,'YYYY-MM-DD') as ap_end_day, ap_dept from tbl_approval )A left join ( select apform_name, apform_no from tbl_apform ) B on fk_apform_no = apform_no left join tbl_mbr on fk_mbr_seq = mbr_seq where fk_mbr_seq=#{fk_mbr_seq}
and (ap_title like '%'||#{word}||'%' or ap_contents like '%'||#{word}||'%')
and ap_progress=0 and ap_progress=1 and ap_progress=2
and ap_start_day >= to_date(#{submitStartDate}, 'YYYY-MM-DD' )
and ap_start_day <= to_date(#{submitEndDate}, 'YYYY-MM-DD' )
order by ap_start_day desc )C where rno between #{startRno} and #{endRno}
